package _05_for_loops._2_badgers;

public class Badgers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int badgers = 12;
		int snake = 1;
		int mushroom = 2;
		int Badgers = 0;
		int Snake = 0;
		int Mushroom = 0;
		for (int i = 0; i < 2; i++) {
			for (Badgers = 0; Badgers < badgers; Badgers += 1) {
				System.out.print("badgers, ");
			}
			System.out.println();
			for (Mushroom = 0; Mushroom < mushroom; Mushroom += 1) {
				System.out.print("mushroom, ");
			}
			System.out.println();
		}
		for (Snake = 0; Snake < snake; Snake += 1) {
			System.out.println(" a snake!");
		}

		// FOR LOOP. Start a for loop to repeat while age <= your age, like this:
		// for ( age=0; age <= yourAge; age +=1 ) {

	}

}
